package org.test.roochblogdemo.rooch.contract.taskservice;

import org.test.roochblogdemo.rooch.contract.repository.*;
import org.test.roochblogdemo.rooch.contract.service.*;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

@Service
public class UpdateBlogStateTaskService {

    @Autowired
    private RoochBlogService roochBlogService;

    @Autowired
    private BlogEventRepository blogEventRepository;

    @Autowired
    private BlogEventService blogEventService;

    @Scheduled(fixedDelayString = "${rooch.contract.update-blog-states.fixed-delay:5000}")
    @Transactional
    public void updateBlogStates() {
        blogEventRepository.findByStatusIsNull().forEach(e -> {
            if (BlogEventService.isDeletionCommand(e)) {
                roochBlogService.deleteBlog(e.getAccountAddress());
            } else {
                roochBlogService.updateBlogState(e.getAccountAddress());
            }
            blogEventService.updateStatusToProcessed(e);
        });
    }
}
